mapper.delete:
delete from tbl_customer
where id in

    #{id}

mapper.select:
select c.id, c.name, u.name as owner, c.phone, c.website
from tbl_customer c join tbl_user u on c.owner = u.id
and c.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and c.website=#{website}
and c.phone =#{phone}
order by c.createTime desc
limit #{skipCount}, #{pageSize}

mapper.insert:
insert into tbl_customer( id, owner, name, website, phone, createBy, createTime, editBy, editTime, contactSummary, nextContactTime, description, address
) values( #{id}, #{owner}, #{name}, #{website}, #{phone}, #{createBy}, #{createTime}, #{editBy}, #{editTime}, #{contactSummary}, #{nextContactTime}, #{description}, #{address}
)
