mapper.select:
select t.id, t.name, u.name as owner, cus.name as customerId, con.fullname as contactsId, t.type, t.source, t.stage from tbl_tran t join tbl_user u on t.owner = u.id left join tbl_customer cus on t.customerId=cus.id left join tbl_contacts con on t.contactsId=con.id
and t.name like '%' #{name} '%'
and u.owner like '%' #{owner} '%'
and t.type like '%' #{type} '%'
and t.source like '%' #{source} '%'
and t.stage like '%' #{stage} '%'
and t.customerId =#{customerId}
and t.contactsId =#{contactsId}
order by t.createTime desc
limit #{skipCount}, #{pageSize}

mapper.update:
update tbl_tran set stage=#{stage},
editBy=#{editBy},editTime=#{editTime}
where id=#{id}

mapper.select:
select tran.id, user.name as owner, tran.money, tran.name, tran.expectedDate, cus.name as customerId, tran.stage, tran.type, tran.source, act.name as activityId, con.fullname as contactsId, tran.createBy, tran.createTime, tran.editBy, tran.editTime, tran.description, tran.contactSummary, tran.nextContactTime
from tbl_tran tran join tbl_user user on tran.owner=user.id left join tbl_customer cus on tran.customerId=cus.id left join tbl_contacts con on tran.contactsId=con.id left join tbl_activity act on tran.activityId=act.id
where tran.id=#{id}

mapper.insert:
insert into tbl_tran( id, owner, money, name, expectedDate, customerId, stage, type, source, activityId, contactsId, createBy, createTime, description, contactSummary, nextContactTime
) values( #{id}, #{owner}, #{money}, #{name}, #{expectedDate}, #{customerId}, #{stage}, #{type}, #{source}, #{activityId}, #{contactsId}, #{createBy}, #{createTime}, #{description}, #{contactSummary}, #{nextContactTime}
)
